// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";


contract Microloan is ReentrancyGuard {
    struct Loan {
        uint256 amount;
        uint256 interestRate; // percentage (e.g., 5 = 5%)
        uint256 repaymentPeriod; // in seconds
        uint256 createdAt;
        address payable borrower;
        address payable lender;
        bool isRepaid;
        bool isFunded;
    }

    mapping(uint256 => Loan) public loans;
    uint256 public loanCounter;

    event LoanRequested(uint256 loanId, address borrower, uint256 amount, uint256 interestRate, uint256 repaymentPeriod);
    event LoanFunded(uint256 loanId, address lender);
    event LoanRepaid(uint256 loanId, address borrower);

    // Borrower creates a loan request
    function requestLoan(uint256 _amount, uint256 _interestRate, uint256 _repaymentPeriod) external {
        require(_amount > 0, "Amount must be greater than zero");
        require(_repaymentPeriod > 0, "Repayment period must be positive");
        uint256 period = _repaymentPeriod * 2592000;
        loanCounter++;
        loans[loanCounter] = Loan({
            amount: _amount,
            interestRate: _interestRate,
            repaymentPeriod: period,
            createdAt: block.timestamp,
            borrower: payable(msg.sender),
            lender: payable(address(0)),
            isRepaid: false,
            isFunded: false
        });

        emit LoanRequested(loanCounter, msg.sender, _amount, _interestRate, _repaymentPeriod);
    }

    // Lender funds the loan
    function fundLoan(uint256 _loanId) external payable nonReentrant {
        Loan storage loan = loans[_loanId];
        require(!loan.isFunded, "Loan already funded");
        require(loan.borrower != msg.sender, "Borrower cannot fund their own loan");
        require(msg.value == loan.amount, "Incorrect funding amount");

        loan.lender = payable(msg.sender);
        loan.isFunded = true;

        // Transfer loan amount to borrower
        loan.borrower.transfer(loan.amount);

        emit LoanFunded(_loanId, msg.sender);
    }

    // Borrower repays the loan
    function repayLoan(uint256 _loanId) external payable nonReentrant {
        Loan storage loan = loans[_loanId];
        require(loan.isFunded, "Loan not funded");
        require(!loan.isRepaid, "Loan already repaid");
        require(msg.sender == loan.borrower, "Only borrower can repay");

        uint256 totalRepayment = loan.amount + (loan.amount * loan.interestRate / 100);
        require(msg.value == totalRepayment, "Incorrect repayment amount");

        loan.isRepaid = true;

        // Transfer repayment to lender
        loan.lender.transfer(msg.value);

        emit LoanRepaid(_loanId, msg.sender);
    }

    // View if a loan is overdue (optional extra function)
    function isLoanOverdue(uint256 _loanId) external view returns (bool) {
        Loan storage loan = loans[_loanId];
        if (!loan.isFunded || loan.isRepaid) {
            return false;
        }
        return (block.timestamp > loan.createdAt + loan.repaymentPeriod);
    }
}

